Python<pre><code>#!/usr/bin/python
# -*- coding: utf-8 -*-

import os
import sys

class Tree:
    def __init__(self):
        self.dirCount = 0
        self.fileCount = 0

    def register(self, absolute):
        if os.path.isdir(absolute):
            self.dirCount += 1
        else:
            self.fileCount += 1

    def summary(self):
        return str(self.dirCount) + " directories, " + str(self.fileCount) + " files"

    def walk(self, directory, prefix = ""):
        filepaths = sorted([filepath for filepath in os.listdir(directory)])

        for index in range(len(filepaths)):
            if filepaths[index][0] == ".":
                continue

            absolute = os.path.join(directory, filepaths[index])
            self.register(absolute)

            if index == len(filepaths) - 1:
                print(prefix + "└── " + filepaths[index])
                if os.path.isdir(absolute):
                    self.walk(absolute, prefix + "    ")
            else:
                print(prefix + "├── " + filepaths[index])
                if os.path.isdir(absolute):
                    self.walk(absolute, prefix + "│   ")

directory = "."
if len(sys.argv) &gt; 1:
    directory = sys.argv[1]
print(directory)

tree = Tree()
tree.walk(directory)
print("\n" + tree.summary())
</code></pre>

C<pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;math.h&gt;
/*选择排序——元素p[a]~p[b]升序排序*/
void selesort(int p[], int a, int b);
/*冒泡排序——整体升序排序*/
void bubsort(int p[], int n);
/*求余矩阵——输入矩阵A，求删掉第0列第i行的矩阵B，其中B是一个空的地址，用来存放矩阵元素*/
void smallermatrix(int a[], int b[], int n, int i);
/*求n阶方阵A行列式——A以一维数组形式输入*/
int det(int a[], int n);
/* 判断素数——输入一个正整数n,是素数返回1，不是返回0 */
int sushu(int n);
/* 矩阵相乘——输入一维数组A m*n, B n*p, 空的已经申请好的数组C m*p，将A*B放在C中 */
void matrixmul(int a[], int b[], int c[], int m, int n, int p);
/* 矩阵转置——输入一维数组A m*n,空的已经申请好的数组C n*m，将A转置放在C中 */
void matrixtran(int a[], int c[], int m, int n);


/*选择排序——元素p[a]~p[b]升序排序*/
void selesort(int p[], int a, int b)
{
    int i, j, k;
    int d;                       //用来交换的变量
    for (i = a; i &lt;= b - 1; i++) //对最后一个元素p[n-1]不用操作
    {
        k = i; //记录现在到哪里了
        for (j = i + 1; j &lt;= b; j++)
            if (p[j] &lt; p[k])
                k = j; //从i的下一个开始找，如果有比i小元素（第j个）的就让k为j
                       //本质目的是找出i后面最小的一个
        if (k != i)    //如果i项不是最小的，那么换！
        {
            d = p[i];
            p[i] = p[k];
            p[k] = d;
        }
    }
}
/*冒泡排序——整体升序排序*/
void bubsort(int p[], int n)
{
    int m, k; //m记录最右边的项，k记录最左边的项
    int j, i;
    int d; //用来交换元素
    k = 0;
    m = n - 1;
    while (k &lt; m)
    {
        j = m - 1;
        m = 0;
        for (i = k; i &lt;= j; i++) //让最大的跑到右边去
            if (p[i] &gt; p[i + 1])
            {
                d = p[i];
                p[i] = p[i + 1];
                p[i + 1] = d;
                m = i; //相当于m在原来的基础上减一
            }
        /*倒着再排*/
        j = k + 1;
        k = 0;
        for (i = m; i &gt;= j; i--) //让最小的跑到左边去
            if (p[i - 1] &gt; p[i])
            {
                d = p[i];
                p[i] = p[i - 1];
                p[i - 1] = d;
                k = i; //相当于k在原来的基础上加一
            }
    }
}
/*求余矩阵——输入矩阵A，求删掉第0列第i行的矩阵B，其中B是一个空的地址，用来存放矩阵元素*/
void smallermatrix(int a[], int b[], int n, int i)
{
    int p, q; //p是A的行数，q是B的列数
    for (p = 0; p &lt;= n - 1; p++)
    {
        if (p &lt; i) //在第i行之前，直接赋值
            for (q = 0; q &lt;= n - 2; q++)
                b[p * (n - 1) + q] = a[p * n + q + 1];
        if (p &gt; i) //在第i行之后，b那里不能往下跳，减个1就行了
            for (q = 0; q &lt;= n - 2; q++)
                b[(p - 1) * (n - 1) + q] = a[p * n + q + 1];
    }
}
/*求n阶方阵A行列式——A以一维数组形式输入*/
int det(int a[], int n) //需要使用上面的smallermatrix函数
{
    int i = 0, j = 0;
    int sum = 0;
    int *b;
    int flag = 1;
    if (n == 2)
        return ((a[0] * a[3]) - (a[1] * a[2])); //针对二阶行列式直接上公式
    else
    {
        for (sum = 0, flag = 1, i = 0; i &lt;= n - 1; i++)
        {
            b = (int *)malloc((n - 1) * (n - 1) * sizeof(int)); //申请B的内存
            smallermatrix(a, b, n, i);                          //求删掉A第0列第i行元素的矩阵，给B
            sum += flag * (a[i * n] * det(b, n - 1));           //累加
            flag *= -1;                                         //每往下一行都要变号，相当于-1的某次方
            free(b);
        }
        return sum;
    }
}
/* 判断素数——输入一个正整数n，是素数返回1，不是返回0 */
int sushu(int n)
{

    int i, flag;
    if (n == 1)
        return 0;
    if (n == 2)
        return 1;
    for (i = 2, flag = 1; i &lt;= sqrt(n) &amp;&amp; flag == 1; i++)
    {
        if (n % i == 0)
            flag = 0; //有一个能整除n，n就不是素数了
    }
    return flag;
}
/* 矩阵相乘——输入一维数组A m*n, B n*p, 空的已经申请好的数组C m*p，将A*B放在C中 */
void matrixmul(int a[], int b[], int c[], int m, int n, int p)
{
    int i, j, k, sum;
    for (i = 0; i &lt;= m - 1; i++)
    {
        for (j = 0, sum = 0; j &lt;= p - 1; j++)
        {
            for (sum = 0, k = 0; k &lt;= n - 1; k++)
            {
                sum += a[i * n + k] * b[k * p + j];
            }
            c[i * p + j] = sum;
        }
    }
}
/* 矩阵转置——输入一维数组A m*n,空的已经申请好的数组C n*m，将A转置放在C中 */
void matrixtran(int a[], int c[], int m, int n)
{
    int i, j;
    for (i = 0; i &lt;= n - 1; i++)
    {
        for (j = 0; j &lt;= m - 1; j++)
        {
            c[i * m + j] = a[j * n + i];
        }
    }
}
/* 大型整数相乘 */
int main()
{
    char num1[1000], num2[1000];
    int a[2000] = {0};
    int n, i, j, k, l1, l2;
    printf("please input two integers:\n");
    scanf("%s%s", num1, num2);
    printf("%s*%s=", num1, num2); //为了之后的输出
    l1 = strlen(num1);
    l2 = strlen(num2);
    /* 将字符转化为数字 */
    for (n = 0; n &lt; l1; n++)
        num1[n] -= '0';
    for (n = 0; n &lt; l2; n++)
        num2[n] -= '0';
    /* 第l1-1位*第l2-1位放在第0位(乘积最小的一位),第0位*第0位放在第l1-1+l2-1位(乘积最大的一位) */
    for (i = 0; i &lt;= l1 - 1; i++)
        for (j = 0; j &lt;= l2 - 1; j++)
            a[l1 - 1 - i + l2 - 1 - j] += num1[i] * num2[j];
    /* 从最小的一位开始——低位置为单位数，并将十位进到高位 */
    for (n = 0; n &lt;= l1 + l2; n++)
    {
        a[n + 1] += a[n] / 10;
        a[n] %= 10;
    }
    /* 从最后往前找第一个非零位 */
    for (n = l1 + l2 + 1; a[n] == 0; n--)
        k = n;
    /* 从非零位的前一位开始打印 */
    for (n = k - 1; n &gt;= 0; n--)
        printf("%d", a[n]);
    printf("\n");
    system("pause");
}
</code></pre>

